%%
%%  This is figure caption style.
%%
\def\figcaption{\refstepcounter\@captype
  \@dblarg{\@figcaption\@captype}}

\long\def\@figcaption#1[#2]#3{\par\addcontentsline{\csname
  ext@#1\endcsname}{#1}{\protect\numberline{\csname
  the#1\endcsname}{\ignorespaces #2}}\begingroup
    \@parboxrestore
    \normalsize
  \@makefigcaption{\csname fnum@#1\endcsname}{\ignorespaces #3}
  \par\endgroup}

\long\def\@makefigcaption#1#2{
 \vskip 10pt
 \setbox\@myboxa\hbox{\small FIG. \thefigure.\kern 0.65em}
   \@mydim=\wd\@myboxa
 \setbox\@myboxb\hbox{\small #2} \advance\@mydim by \wd\@myboxb
 \ifdim \@mydim > \hsize \advance\@mydim by -\wd\@myboxb
   \renewcommand\baselinestretch{1}\small\normalsize
 {\parskip=0pt\noindent\unhbox\@myboxa\par}
 \vskip-8.5pt
 \noindent\@shiftdim=\hsize \advance\@shiftdim by -\@mydim
 \hskip\@mydim\vbox{
  \hsize=\@shiftdim{\noindent\unhbox\@myboxb}}
  \else \hbox to \hsize{\hfil\box\@myboxa\box\@myboxb\hfil}
 \fi\vskip-6pt}

%%
%%  This is table caption style.
%%
\def\tblcaption{\refstepcounter\@captype
  \@dblarg{\@tblcaption\@captype}}

\long\def\@tblcaption#1[#2]#3{\par\addcontentsline{\csname
  ext@#1\endcsname}{#1}{\protect\numberline{\csname
  the#1\endcsname}{\ignorespaces #2}}\begingroup
    \@parboxrestore
    \normalsize
  \@maketblcaption{\csname fnum@#1\endcsname}{\ignorespaces #3}
  \par\endgroup}

\long\def\@maketblcaption#1#2{
 \vskip 10pt
 \setbox\@myboxa\hbox{TABLE \@Roman\c@table.\kern 0.65em}
 \@mydim=\wd\@myboxa
  \setbox\@myboxb\hbox{#2} \advance\@mydim by \wd\@myboxb
 \ifdim \@mydim > \hsize \advance\@mydim by -\wd\@myboxb
  \renewcommand\baselinestretch{1}\small\normalsize
 \noindent\hangindent\@mydim\hangafter=1
 \unhbox\@myboxa\unhbox\@myboxb\par
 \else \hbox to \hsize{\hfil\box\@myboxa\box\@myboxb\hfil}
 \fi}
